import React, { useState, useCallback } from "react";

import Header from "./components/Header";
import Footer from "./components/Footer";
import Controls from "./components/Controls";
import Visualizer from "./components/Visualizer";

import {
  IconPlay,
  IconPause,
  IconRotateCcw,
  IconStepForward
} from "./components/Icons";

export default function App() {
  // state
  const [selectedAlgo, setSelectedAlgo] = useState("bubbleSort");
  const [arraySize, setArraySize] = useState(5);
  const [speed, setSpeed] = useState(80);
  const [arrayInput, setArrayInput] = useState("");
  const [isPlaying, setIsPlaying] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);

  const [currentStep, setCurrentStep] = useState(0);
  const [isAtEnd, setIsAtEnd] = useState(false);

  // ✅ Visualizer receives events from App
  const handleReset = () => {
    window.dispatchEvent(new CustomEvent("sort-reset"));
    setIsPlaying(false);
    setIsAtEnd(false);
    setCurrentStep(0);
  };

  const handlePlayPause = () => {
    if (isPlaying) {
      setIsPlaying(false);
      window.dispatchEvent(new CustomEvent("sort-pause"));
    } else {
      setIsPlaying(true);
      window.dispatchEvent(new CustomEvent("sort-play", { detail: { speed } }));
    }
  };

  const handleNextStep = () => {
    window.dispatchEvent(new CustomEvent("sort-next"));
  };

  const handleSizeChange = (e) => {
    setArraySize(Number(e.target.value));
    handleReset();
  };

  const handleSpeedChange = (e) => {
    const val = Number(e.target.value);
    setSpeed(105 - val);
  };

  const handleAlgoChange = (e) => {
    setSelectedAlgo(e.target.value);
    handleReset();
  };

  const setArrayFromInput = () => {
    const input = (arrayInput ?? "").trim();
    window.dispatchEvent(new CustomEvent("sort-set-array", { detail: input }));
  };

  const handleSaveSort = () => {
    // backend API call future
    console.log("Saving sort to backend...");
  };

  return (
    <div className="app">
      <Header />

      <div className="container">
        <Controls
          // state
          selectedAlgo={selectedAlgo}
          handleAlgoChange={handleAlgoChange}
          arrayInput={arrayInput}
          setArrayInput={setArrayInput}
          setArrayFromInput={setArrayFromInput}
          arraySize={arraySize}
          handleSizeChange={handleSizeChange}
          speed={speed}
          handleSpeedChange={handleSpeedChange}

          // core controls
          handleReset={handleReset}
          handlePlayPause={handlePlayPause}
          handleNextStep={handleNextStep}

          // status
          isPlaying={isPlaying}
          isGenerating={isGenerating}
          isAtEnd={isAtEnd}
          currentStep={currentStep}

          // icons
          IconPlay={IconPlay}
          IconPause={IconPause}
          IconRotateCcw={IconRotateCcw}
          IconStepForward={IconStepForward}

          // MERN save feature
          handleSaveSort={handleSaveSort}
        />

        <Visualizer
          selectedAlgo={selectedAlgo}
          arraySize={arraySize}
          speed={speed}
          isPlaying={isPlaying}
          setIsPlaying={setIsPlaying}
          setCurrentStep={setCurrentStep}
          setIsAtEnd={setIsAtEnd}
          setIsGenerating={setIsGenerating}
        />
      </div>

      <Footer />
    </div>
  );
}
