import React from 'react';

// --- Icon Components (using inline SVG) ---
const IconSave = () => (
  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" 
    viewBox="0 0 24 24" fill="none" stroke="currentColor" 
    strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" 
    className="w-5 h-5">
    <path d="M19 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h11l5 5v11a2 2 0 0 1-2 2z"></path>
    <polyline points="17 21 17 13 7 13 7 21"></polyline>
    <polyline points="7 3 7 8 15 8"></polyline>
  </svg>
);

export default function Controls({
  selectedAlgo,
  handleAlgoChange,
  arrayInput,
  setArrayInput,
  setArrayFromInput,
  arraySize,
  handleSizeChange,
  speed,
  handleSpeedChange,
  handleReset,
  handlePlayPause,
  handleNextStep,
  isPlaying,
  isGenerating,
  isAtEnd,
  currentStep,

  // icons
  IconPlay,
  IconPause,
  IconRotateCcw,
  IconStepForward,

  // MERN
  handleSaveSort
}) {
  return (
    <div className="w-full max-w-7xl mx-auto p-4 bg-gray-800 rounded-lg shadow-md my-4 flex flex-col gap-4">

      {/* Row 1 */}
      <div className="flex flex-col md:flex-row gap-4">

        {/* Algorithm Dropdown */}
        <div className="flex-1">
          <label className="block text-sm font-medium mb-1">Algorithm</label>
          <select
            value={selectedAlgo}
            onChange={handleAlgoChange}
            disabled={isPlaying || isGenerating}
            className="w-full p-2 bg-gray-700 border border-gray-600 rounded-lg"
          >
            <option value="bubbleSort">Bubble Sort</option>
            <option value="selectionSort">Selection Sort</option>
            <option value="insertionSort">Insertion Sort</option>
            <option value="mergeSort">Merge Sort</option>
            <option value="quickSort">Quick Sort</option>
          </select>
        </div>

        {/* Custom Array */}
        <div className="flex-1">
          <label className="block text-sm font-medium mb-1">Custom Array (comma-separated)</label>
          <div className="flex gap-2">
            <input
              type="text"
              value={arrayInput ?? ""}      // ✅ FIXED — always safe
              onChange={(e) => setArrayInput(e.target.value)}
              placeholder="e.g., 8, 2, 5, 1"
              disabled={isPlaying || isGenerating}
              className="flex-1 p-2 bg-gray-700 border border-gray-600 rounded-lg"
            />
            <button
              onClick={setArrayFromInput}
              disabled={isPlaying || isGenerating}
              className="px-4 py-2 bg-indigo-600 hover:bg-indigo-700 text-white font-semibold rounded-lg"
            >
              Set
            </button>
          </div>
        </div>

      </div>

      {/* Row 2 */}
      <div className="flex flex-col md:flex-row gap-4 items-center justify-between">

        {/* Sliders */}
        <div className="flex flex-col sm:flex-row gap-4 w-full md:w-auto">

          <div className="flex-1 min-w-[150px]">
            <label className="block text-sm font-medium mb-1">
              Array Size ({arraySize})
            </label>
            <input
              type="range"
              min="3"
              max="8"
              value={arraySize}
              onChange={handleSizeChange}
              disabled={isPlaying || isGenerating}
              className="w-full h-2 bg-gray-700 rounded-lg"
            />
          </div>

          <div className="flex-1 min-w-[150px]">
            <label className="block text-sm font-medium mb-1">
              Speed (for Play)
            </label>
            <input
              type="range"
              min="5"
              max="100"
              value={105 - speed}
              onChange={handleSpeedChange}
              disabled={isGenerating}
              className="w-full h-2 bg-gray-700 rounded-lg"
            />
          </div>

        </div>

        {/* Buttons */}
        <div className="flex gap-2 flex-wrap justify-center">

          {/* Save */}
          <button
            onClick={handleSaveSort}
            disabled={
              isPlaying || 
              isGenerating || 
              (arrayInput ?? "").trim() === ""     // ✅ FIXED
            }
            className="flex items-center gap-2 px-4 py-2 bg-purple-600 hover:bg-purple-700 text-white font-semibold rounded-lg disabled:opacity-50"
          >
            <IconSave />
            <span className="hidden sm:inline">Save</span>
          </button>

          {/* Randomize */}
          <button
            onClick={handleReset}
            disabled={isPlaying || isGenerating}
            className="flex items-center gap-2 px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-lg"
          >
            <IconRotateCcw />
            <span className="hidden sm:inline">Randomize</span>
          </button>

          {/* Play / Pause */}
          <button
            onClick={handlePlayPause}
            disabled={isGenerating || (isAtEnd && !isPlaying)}
            className={`flex items-center gap-2 px-4 py-2 font-semibold rounded-lg w-[100px] justify-center ${
              isPlaying 
                ? 'bg-yellow-500 hover:bg-yellow-600 text-gray-900' 
                : 'bg-green-500 hover:bg-green-600 text-white'
            }`}
          >
            {isPlaying ? <IconPause /> : <IconPlay />}
            <span className="hidden sm:inline">
              {isPlaying ? "Pause" : (currentStep > 0 && !isAtEnd ? "Resume" : "Play")}
            </span>
          </button>

          {/* Next Step */}
          <button
            onClick={handleNextStep}
            disabled={isPlaying || isGenerating || isAtEnd}
            className="flex items-center gap-2 px-4 py-2 bg-gray-600 hover:bg-gray-700 text-white font-semibold rounded-lg"
          >
            <IconStepForward />
            <span className="hidden sm:inline">Next</span>
          </button>

        </div>
      </div>

    </div>
  );
}
