import React, { useState, useEffect } from 'react';
import './styles/SortingVisualizer.css';

const SortingVisualizer = () => {
  const [array, setArray] = useState([]);
  const [algorithm, setAlgorithm] = useState('bubbleSort');
  const [speed, setSpeed] = useState(50);
  const [isSorting, setIsSorting] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [arraySize, setArraySize] = useState(50);
  const [comparingIndices, setComparingIndices] = useState([]);
  const [sortedIndices, setSortedIndices] = useState([]);

  // Initialize array
  useEffect(() => {
    resetArray();
  }, [arraySize]);

  const resetArray = () => {
    const newArray = [];
    for (let i = 0; i < arraySize; i++) {
      newArray.push(randomIntFromInterval(5, 400));
    }
    setArray(newArray);
    setComparingIndices([]);
    setSortedIndices([]);
  };

  const randomIntFromInterval = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1) + min);
  };

  const sleep = (ms) => {
    return new Promise(resolve => setTimeout(resolve, ms));
  };

  // Bubble Sort Algorithm
  const bubbleSort = async () => {
    setIsSorting(true);
    setIsPaused(false);
    const arr = [...array];
    const n = arr.length;
    let sorted = [...sortedIndices];

    for (let i = 0; i < n - 1; i++) {
      for (let j = 0; j < n - i - 1; j++) {
        if (isPaused) {
          while (isPaused) await sleep(100);
        }

        setComparingIndices([j, j + 1]);
        
        if (arr[j] > arr[j + 1]) {
          // Swap elements
          [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
          setArray([...arr]);
        }

        await sleep(300 - speed * 3);
      }
      sorted.push(n - i - 1);
      setSortedIndices([...sorted]);
    }
    sorted.push(0);
    setSortedIndices([...sorted]);
    setComparingIndices([]);
    setIsSorting(false);
  };

  // Selection Sort Algorithm
  const selectionSort = async () => {
    setIsSorting(true);
    setIsPaused(false);
    const arr = [...array];
    const n = arr.length;
    let sorted = [...sortedIndices];

    for (let i = 0; i < n - 1; i++) {
      let minIdx = i;
      
      for (let j = i + 1; j < n; j++) {
        if (isPaused) {
          while (isPaused) await sleep(100);
        }

        setComparingIndices([minIdx, j]);
        
        if (arr[j] < arr[minIdx]) {
          minIdx = j;
        }
        
        await sleep(300 - speed * 3);
      }

      if (minIdx !== i) {
        [arr[i], arr[minIdx]] = [arr[minIdx], arr[i]];
        setArray([...arr]);
      }
      
      sorted.push(i);
      setSortedIndices([...sorted]);
    }
    sorted.push(n - 1);
    setSortedIndices([...sorted]);
    setComparingIndices([]);
    setIsSorting(false);
  };

  // Insertion Sort Algorithm
  const insertionSort = async () => {
    setIsSorting(true);
    setIsPaused(false);
    const arr = [...array];
    const n = arr.length;
    let sorted = [...sortedIndices];

    for (let i = 1; i < n; i++) {
      let key = arr[i];
      let j = i - 1;

      sorted.push(i - 1);
      setSortedIndices([...sorted]);

      while (j >= 0 && arr[j] > key) {
        if (isPaused) {
          while (isPaused) await sleep(100);
        }

        setComparingIndices([j, j + 1]);
        
        arr[j + 1] = arr[j];
        j = j - 1;
        
        setArray([...arr]);
        await sleep(300 - speed * 3);
      }
      arr[j + 1] = key;
      setArray([...arr]);
    }
    
    sorted.push(n - 1);
    setSortedIndices([...sorted]);
    setComparingIndices([]);
    setIsSorting(false);
  };

  // Quick Sort Algorithm
  const quickSort = async () => {
    setIsSorting(true);
    setIsPaused(false);
    const arr = [...array];
    await quickSortHelper(arr, 0, arr.length - 1);
    setSortedIndices(Array.from({ length: arr.length }, (_, i) => i));
    setComparingIndices([]);
    setIsSorting(false);
  };

  const quickSortHelper = async (arr, low, high) => {
    if (low < high) {
      const pi = await partition(arr, low, high);
      await quickSortHelper(arr, low, pi - 1);
      await quickSortHelper(arr, pi + 1, high);
    }
  };

  const partition = async (arr, low, high) => {
    const pivot = arr[high];
    let i = low - 1;

    for (let j = low; j < high; j++) {
      if (isPaused) {
        while (isPaused) await sleep(100);
      }

      setComparingIndices([j, high]);
      
      if (arr[j] < pivot) {
        i++;
        [arr[i], arr[j]] = [arr[j], arr[i]];
        setArray([...arr]);
      }
      
      await sleep(300 - speed * 3);
    }
    
    [arr[i + 1], arr[high]] = [arr[high], arr[i + 1]];
    setArray([...arr]);
    return i + 1;
  };

  // Merge Sort Algorithm
  const mergeSort = async () => {
    setIsSorting(true);
    setIsPaused(false);
    const arr = [...array];
    await mergeSortHelper(arr, 0, arr.length - 1);
    setSortedIndices(Array.from({ length: arr.length }, (_, i) => i));
    setComparingIndices([]);
    setIsSorting(false);
  };

  const mergeSortHelper = async (arr, l, r) => {
    if (l >= r) return;
    
    const m = Math.floor((l + r) / 2);
    await mergeSortHelper(arr, l, m);
    await mergeSortHelper(arr, m + 1, r);
    await merge(arr, l, m, r);
  };

  const merge = async (arr, l, m, r) => {
    const n1 = m - l + 1;
    const n2 = r - m;
    
    const L = new Array(n1);
    const R = new Array(n2);
    
    for (let i = 0; i < n1; i++) L[i] = arr[l + i];
    for (let j = 0; j < n2; j++) R[j] = arr[m + 1 + j];
    
    let i = 0, j = 0, k = l;
    
    while (i < n1 && j < n2) {
      if (isPaused) {
        while (isPaused) await sleep(100);
      }

      setComparingIndices([l + i, m + 1 + j]);
      
      if (L[i] <= R[j]) {
        arr[k] = L[i];
        i++;
      } else {
        arr[k] = R[j];
        j++;
      }
      
      setArray([...arr]);
      await sleep(300 - speed * 3);
      k++;
    }
    
    while (i < n1) {
      arr[k] = L[i];
      i++;
      k++;
      setArray([...arr]);
      await sleep(300 - speed * 3);
    }
    
    while (j < n2) {
      arr[k] = R[j];
      j++;
      k++;
      setArray([...arr]);
      await sleep(300 - speed * 3);
    }
  };

  const handleSort = () => {
    switch (algorithm) {
      case 'bubbleSort':
        bubbleSort();
        break;
      case 'selectionSort':
        selectionSort();
        break;
      case 'insertionSort':
        insertionSort();
        break;
      case 'quickSort':
        quickSort();
        break;
      case 'mergeSort':
        mergeSort();
        break;
      default:
        bubbleSort();
    }
  };

  const handlePauseResume = () => {
    setIsPaused(!isPaused);
  };

  return (
    <div className="sorting-visualizer">
      <div className="controls">
        <div className="control-group">
          <label>Algorithm:</label>
          <select 
            value={algorithm} 
            onChange={(e) => setAlgorithm(e.target.value)}
            disabled={isSorting}
          >
            <option value="bubbleSort">Bubble Sort</option>
            <option value="selectionSort">Selection Sort</option>
            <option value="insertionSort">Insertion Sort</option>
            <option value="quickSort">Quick Sort</option>
            <option value="mergeSort">Merge Sort</option>
          </select>
        </div>

        <div className="control-group">
          <label>Speed:</label>
          <input
            type="range"
            min="1"
            max="100"
            value={speed}
            onChange={(e) => setSpeed(e.target.value)}
            disabled={isSorting}
          />
        </div>

        <div className="control-group">
          <label>Array Size:</label>
          <input
            type="range"
            min="10"
            max="100"
            value={arraySize}
            onChange={(e) => setArraySize(e.target.value)}
            disabled={isSorting}
          />
        </div>

        <div className="control-group buttons">
          <button 
            onClick={resetArray} 
            disabled={isSorting}
            className="btn reset"
          >
            Reset Array
          </button>
          
          <button 
            onClick={handleSort} 
            disabled={isSorting}
            className="btn sort"
          >
            Start Sorting
          </button>
          
          {isSorting && (
            <button 
              onClick={handlePauseResume}
              className="btn pause"
            >
              {isPaused ? 'Resume' : 'Pause'}
            </button>
          )}
        </div>
      </div>

      <div className="array-container">
        {array.map((value, idx) => {
          const isComparing = comparingIndices.includes(idx);
          const isSorted = sortedIndices.includes(idx);
          
          let barColor = '#3b82f6'; // Default blue
          if (isComparing) barColor = '#ef4444'; // Red for comparing
          if (isSorted) barColor = '#10b981'; // Green for sorted

          return (
            <div
              className="array-bar"
              key={idx}
              style={{
                height: `${value}px`,
                backgroundColor: barColor,
                width: `${100 / arraySize}%`,
              }}
            >
              <span className="bar-value">{value}</span>
            </div>
          );
        })}
      </div>

      <div className="algorithm-info">
        <h3>Algorithm Information</h3>
        <div className="info-grid">
          <div className="info-item">
            <strong>Time Complexity:</strong>
            <span>
              {algorithm === 'bubbleSort' && 'O(n²)'}
              {algorithm === 'selectionSort' && 'O(n²)'}
              {algorithm === 'insertionSort' && 'O(n²)'}
              {algorithm === 'quickSort' && 'O(n log n)'}
              {algorithm === 'mergeSort' && 'O(n log n)'}
            </span>
          </div>
          <div className="info-item">
            <strong>Space Complexity:</strong>
            <span>
              {algorithm === 'bubbleSort' && 'O(1)'}
              {algorithm === 'selectionSort' && 'O(1)'}
              {algorithm === 'insertionSort' && 'O(1)'}
              {algorithm === 'quickSort' && 'O(log n)'}
              {algorithm === 'mergeSort' && 'O(n)'}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SortingVisualizer;
