import React, { useEffect, useRef, useState } from "react";
import {
  bubbleSortSteps,
  selectionSortSteps,
  insertionSortSteps,
  mergeSortSteps,
  quickSortSteps,
  createRandomArray,
  sleep,
} from "../utils/sortingUtils";
import { ALGO_INFO } from "../data/algorithmInfo";

const ALGO_MAP = {
  bubbleSort: bubbleSortSteps,
  selectionSort: selectionSortSteps,
  insertionSort: insertionSortSteps,
  mergeSort: mergeSortSteps,
  quickSort: quickSortSteps,
};

export default function Visualizer({
  algorithm,
  size,
  speed,
  isRunning,
  setIsRunning,
  controlResetSignal,
}) {
  const [array, setArray] = useState(() => createRandomArray(size, 200));
  const [stepIndex, setStepIndex] = useState(0);
  const [steps, setSteps] = useState([]);
  const [highlight, setHighlight] = useState([]);
  const timerRef = useRef(null);

  // Build steps when algorithm/array changes or when shuffle/reset called
  useEffect(() => {
    setArray(createRandomArray(size, 200));
    setStepIndex(0);
    setSteps([]);
    setHighlight([]);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [size, controlResetSignal]);

  // Generate steps whenever algorithm or array changes (manual generation)
  useEffect(() => {
    const fn = ALGO_MAP[algorithm];
    if (!fn) return;
    const s = fn(array);
    setSteps(s);
    setStepIndex(0);
    setHighlight([]);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [algorithm, array]);

  // Play animation when isRunning toggles
  useEffect(() => {
    if (isRunning) {
      runAnimation();
    } else {
      if (timerRef.current) {
        clearTimeout(timerRef.current);
        timerRef.current = null;
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isRunning, steps, stepIndex, speed]);

  const runAnimation = async () => {
    if (!steps || steps.length === 0) return;
    if (stepIndex >= steps.length) {
      setIsRunning(false);
      return;
    }
    // Schedule next step
    const next = () => {
      const s = steps[stepIndex];
      setArray(s.array);
      setHighlight(s.highlights || []);
      setStepIndex((p) => p + 1);
    };

    next();
    timerRef.current = setTimeout(() => {
      if (isRunning) {
        // recursion handled by effect (stepIndex changed)
        // but we call runAnimation to continue chain
        runAnimation();
      }
    }, Math.max(8, speed));
  };

  // Step once (manual)
  const stepOnce = () => {
    if (!steps || steps.length === 0) return;
    const idx = Math.min(stepIndex, steps.length - 1);
    const s = steps[idx];
    setArray(s.array);
    setHighlight(s.highlights || []);
    setStepIndex((p) => Math.min(p + 1, steps.length));
  };

  // External shuffle (exposed via props in App)
  const shuffle = () => {
    const arr = createRandomArray(size, 200);
    setArray(arr);
    setSteps([]);
    setStepIndex(0);
    setHighlight([]);
  };

  // Expose shuffle via custom event on window (simple)
  useEffect(() => {
    const handler = (e) => {
      if (e.detail === "shuffle") shuffle();
      if (e.detail === "reset") {
        const arr = createRandomArray(size, 200);
        setArray(arr);
        setSteps([]);
        setStepIndex(0);
        setHighlight([]);
      }
    };
    window.addEventListener("sorting-control", handler);
    return () => window.removeEventListener("sorting-control", handler);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [size]);

  const maxVal = Math.max(...array, 1);

  return (
    <div className="card" style={{ minHeight: 420 }}>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <div style={{ fontWeight: 700 }}>{ALGO_INFO[algorithm]?.title || "Algorithm"}</div>
        <div style={{ color: "var(--muted)" }}>
          Step {Math.min(stepIndex, steps.length)}/{steps.length}
        </div>
      </div>

      <div className="visualizer-area" style={{ marginTop: 12 }}>
        {array.map((v, idx) => {
          const width = Math.max(6, Math.floor(1000 / array.length));
          const height = Math.max(6, Math.floor((v / maxVal) * 100));
          const isHighlighted = highlight && highlight.includes(idx);
          return (
            <div
              key={idx}
              className={`bar ${isHighlighted ? "alt" : ""}`}
              title={v}
              style={{
                height: `${height}%`,
                width: `${width}px`,
                minWidth: `${Math.min(20, width)}px`,
              }}
            >
              {/* optional small value label */}
            </div>
          );
        })}
      </div>

      <div className="description">
        <div style={{ marginTop: 8 }}>{ALGO_INFO[algorithm]?.description}</div>
        <pre style={{ marginTop: 8, background: "transparent", color: "var(--muted)", fontSize: 12 }}>
          {ALGO_INFO[algorithm]?.pseudoCode}
        </pre>
      </div>
    </div>
  );
}
