// Utility to create a random array of given length and max value
export const createRandomArray = (length = 40, maxVal = 200) => {
  const arr = Array.from({ length }, () => Math.floor(Math.random() * maxVal) + 5);
  return arr;
};

export const sleep = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

/*
  Each algorithm below returns an array of "steps".
  Each step is { array: [...], highlights: [i, j], swapped: boolean }
  Visualizer will iterate steps and animate.
*/

export function bubbleSortSteps(input) {
  const arr = input.slice();
  const steps = [];
  const n = arr.length;
  for (let i = 0; i < n - 1; i++) {
    for (let j = 0; j < n - i - 1; j++) {
      steps.push({ array: arr.slice(), highlights: [j, j + 1], swapped: false });
      if (arr[j] > arr[j + 1]) {
        [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
        steps.push({ array: arr.slice(), highlights: [j, j + 1], swapped: true });
      }
    }
  }
  steps.push({ array: arr.slice(), highlights: [], swapped: false });
  return steps;
}

export function selectionSortSteps(input) {
  const arr = input.slice();
  const steps = [];
  const n = arr.length;
  for (let i = 0; i < n; i++) {
    let minIdx = i;
    for (let j = i + 1; j < n; j++) {
      steps.push({ array: arr.slice(), highlights: [minIdx, j], swapped: false });
      if (arr[j] < arr[minIdx]) {
        minIdx = j;
        steps.push({ array: arr.slice(), highlights: [minIdx, j], swapped: false });
      }
    }
    if (minIdx !== i) {
      [arr[i], arr[minIdx]] = [arr[minIdx], arr[i]];
      steps.push({ array: arr.slice(), highlights: [i, minIdx], swapped: true });
    }
  }
  steps.push({ array: arr.slice(), highlights: [], swapped: false });
  return steps;
}

export function insertionSortSteps(input) {
  const arr = input.slice();
  const steps = [];
  for (let i = 1; i < arr.length; i++) {
    let key = arr[i];
    let j = i - 1;
    steps.push({ array: arr.slice(), highlights: [i], swapped: false });
    while (j >= 0 && arr[j] > key) {
      arr[j + 1] = arr[j];
      j = j - 1;
      steps.push({ array: arr.slice(), highlights: [j + 1, j + 2], swapped: true });
    }
    arr[j + 1] = key;
    steps.push({ array: arr.slice(), highlights: [j + 1], swapped: false });
  }
  steps.push({ array: arr.slice(), highlights: [], swapped: false });
  return steps;
}

export function mergeSortSteps(input) {
  const arr = input.slice();
  const steps = [];
  const aux = arr.slice();

  function merge(l, m, r) {
    let i = l, j = m + 1, k = l;
    while (i <= m && j <= r) {
      if (aux[i] <= aux[j]) {
        arr[k++] = aux[i++];
      } else {
        arr[k++] = aux[j++];
      }
      steps.push({ array: arr.slice(), highlights: [i - 1, j - 1], swapped: false });
    }
    while (i <= m) {
      arr[k++] = aux[i++];
      steps.push({ array: arr.slice(), highlights: [i - 1], swapped: false });
    }
    while (j <= r) {
      arr[k++] = aux[j++];
      steps.push({ array: arr.slice(), highlights: [j - 1], swapped: false });
    }
    for (let s = l; s <= r; s++) aux[s] = arr[s];
  }

  function mergeSortHelper(l, r) {
    if (l >= r) return;
    const m = Math.floor((l + r) / 2);
    mergeSortHelper(l, m);
    mergeSortHelper(m + 1, r);
    merge(l, m, r);
  }

  // initialize aux
  for (let i = 0; i < arr.length; i++) aux[i] = arr[i];
  mergeSortHelper(0, arr.length - 1);
  steps.push({ array: arr.slice(), highlights: [], swapped: false });
  return steps;
}

export function quickSortSteps(input) {
  const arr = input.slice();
  const steps = [];

  function partition(l, h) {
    const pivot = arr[h];
    let i = l - 1;
    for (let j = l; j < h; j++) {
      steps.push({ array: arr.slice(), highlights: [j, h], swapped: false });
      if (arr[j] < pivot) {
        i++;
        [arr[i], arr[j]] = [arr[j], arr[i]];
        steps.push({ array: arr.slice(), highlights: [i, j], swapped: true });
      }
    }
    [arr[i + 1], arr[h]] = [arr[h], arr[i + 1]];
    steps.push({ array: arr.slice(), highlights: [i + 1, h], swapped: true });
    return i + 1;
  }

  function quickSortHelper(l, h) {
    if (l < h) {
      const p = partition(l, h);
      quickSortHelper(l, p - 1);
      quickSortHelper(p + 1, h);
    }
  }

  quickSortHelper(0, arr.length - 1);
  steps.push({ array: arr.slice(), highlights: [], swapped: false });
  return steps;
}
