import React from 'react';

// --- Color Helper Functions ---
const getBarColor = (idx, highlights, sorted) => {
    if (sorted.includes(idx)) {
      return 'bg-green-500';
    }
    const highlight = highlights[idx];
    if (highlight) {
      switch (highlight) {
        case 'compare': return 'bg-yellow-400';
        case 'swap': return 'bg-red-500';
        case 'pivot': return 'bg-purple-500';
        case 'range': return 'bg-cyan-400';
      }
    }
    return 'bg-blue-400';
};

const getBoxColor = (idx, highlights, sorted) => {
    // This function is identical to getBarColor, but kept separate for clarity
    if (sorted.includes(idx)) {
      return 'bg-green-500';
    }
    const highlight = highlights[idx];
    if (highlight) {
      switch (highlight) {
        case 'compare': return 'bg-yellow-400';
        case 'swap': return 'bg-red-500';
        case 'pivot': return 'bg-purple-500';
        case 'range': return 'bg-cyan-400';
      }
    }
    return 'bg-blue-400';
};

// Add default empty values for props
const defaultDisplay = { array: [], highlights: {}, sorted: [] };

export default function Visualizer({ 
  currentDisplay = defaultDisplay, // Add default value
  maxVal = 100, 
  rowVisualizerRef, 
  stepsToDisplay = [] // Add default value
}) {
  return (
    <>
      {/* 1. Bar Visualizer (Top) */}
      <main className="w-full max-w-7xl mx-auto bg-gray-800 rounded-lg shadow-inner p-4 flex items-end justify-center min-h-[30vh]">
        <div className="flex w-full h-full items-end gap-1">
          {/* Add optional chaining for safety */}
          {currentDisplay?.array.map((value, idx) => (
            <div
              key={idx}
              className={`flex-1 rounded-t-sm transition-all duration-150 ${getBarColor(idx, currentDisplay.highlights, currentDisplay.sorted)}`}
              style={{ height: `${(value / maxVal) * 100}%` }}
              title={`Value: ${value}`}
            >
               <span className="text-black text-xs block text-center pt-1 font-medium opacity-80">{value}</span>
            </div>
          ))}
        </div>
      </main>
      
      {/* 2. Row Visualizer (Bottom) */}
      <main 
        ref={rowVisualizerRef}
        className="flex-1 w-full max-w-7xl mx-auto bg-gray-800 rounded-lg shadow-inner p-4 flex flex-col min-h-[40vh] md:min-h-[50vh] overflow-y-auto mt-4"
      >
        <div className="flex flex-col gap-2">
          {/* Add optional chaining for safety */}
          {stepsToDisplay?.map((step, stepIndex) => (
            <div 
              key={stepIndex} 
              className="flex gap-2 justify-center items-center p-2 bg-gray-700 rounded-lg"
            >
              <span className="font-mono text-sm text-gray-400 mr-2">
                {String(stepIndex).padStart(3, '0')}:
              </span>
              {/* Add optional chaining for safety */}
              {step?.array.map((value, idx) => (
                <div
                  key={idx}
                  className={`w-12 h-12 flex items-center justify-center rounded font-bold text-black text-lg transition-all duration-150 ${getBoxColor(idx, step.highlights, step.sorted)}`}
                  title={`Index: ${idx}, Value: ${value}`}
                >
                  {value}
                </div>
              ))}
            </div>
          ))}
        </div>
      </main>
    </>
  );
}
