import React, { useState, useCallback } from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Controls from "./components/Controls";
import Visualizer from "./components/Visualizer";

/*
  App connects controls and visualizer.
  We preserve the sorting behavior and only separate UI into components.
*/

export default function App() {
  const [algorithm, setAlgorithm] = useState("bubbleSort");
  const [size, setSize] = useState(40);
  const [speed, setSpeed] = useState(80); // ms per step
  const [isRunning, setIsRunning] = useState(false);
  const [resetSignal, setResetSignal] = useState(0);

  const triggerShuffle = useCallback(() => {
    // use custom event to notify Visualizer
    window.dispatchEvent(new CustomEvent("sorting-control", { detail: "shuffle" }));
    setIsRunning(false);
  }, []);

  const triggerReset = useCallback(() => {
    window.dispatchEvent(new CustomEvent("sorting-control", { detail: "reset" }));
    setResetSignal((n) => n + 1);
    setIsRunning(false);
  }, []);

  const handlePlay = () => {
    setIsRunning(true);
  };

  const handlePause = () => {
    setIsRunning(false);
  };

  const handleStep = () => {
    // dispatch a one-time play for a single step; Visualizer listens for isRunning false and acts via Controls calling Visualizer.stepOnce through a custom event
    window.dispatchEvent(new CustomEvent("sorting-step", {}));
  };

  return (
    <div className="app">
      <Header />
      <div className="container">
        <div>
          <Controls
            algorithm={algorithm}
            setAlgorithm={setAlgorithm}
            size={size}
            setSize={setSize}
            speed={speed}
            setSpeed={setSpeed}
            onShuffle={triggerShuffle}
            onPlay={handlePlay}
            onPause={handlePause}
            onStep={handleStep}
            onReset={triggerReset}
            isRunning={isRunning}
          />
        </div>

        <div>
          <Visualizer
            algorithm={algorithm}
            size={size}
            speed={speed}
            isRunning={isRunning}
            setIsRunning={setIsRunning}
            controlResetSignal={resetSignal}
          />
        </div>
      </div>

      <Footer />
    </div>
  );
}
